package java8;

public class BinaryToDecimal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=12;//Decimal
     String b=Integer.toBinaryString(a);
     System.out.println(b);//decimal to binary
     
     String f=Integer.toHexString(a);
     System.out.println(f); //decimal to Hexa Decimal
     
     String c=Integer.toOctalString(a);
     System.out.println(c); //Decimal to Octal
     
	}

}
